import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import BookDetails from './book/BookDetails';
import './DashBoard.css';
const Dashboard = () => {
  const [books, setBooks] = useState([]);
  const [users, setUsers] = useState([]);
  const [loadingBooks, setLoadingBooks] = useState(true);
  const [loadingUsers, setLoadingUsers] = useState(true);
  const [showBooks, setShowBooks] = useState(false);
  const [showUsers, setShowUsers] = useState(false);
  const [authorQuery, setAuthorQuery] = useState('');
  const [bookNameQuery, setBookNameQuery] = useState('');
  const [genreQuery, setGenreQuery] = useState('');
  const [selectedBook, setSelectedBook] = useState(null);

  useEffect(() => {
    fetchBooks();
    fetchUsers();
  }, []);

  const fetchBooks = async () => {
    try {
      const response = await axios.get('http://localhost:8080/api/books');
      setBooks(response.data);
      setLoadingBooks(false);
    } catch (error) {
      console.error('Error fetching books:', error);
    }
  };

  const fetchUsers = async () => {
    try {
      const response = await axios.get('http://localhost:8080/api/users');
      setUsers(response.data);
      setLoadingUsers(false);
    } catch (error) {
      console.error('Error fetching users:', error);
    }
  };

  const toggleBooks = () => {
    setShowBooks(!showBooks);
    // Hide user details when toggling books
    setShowUsers(false);
  };

  const toggleUsers = () => {
    setShowUsers(!showUsers);
    // Hide book details when toggling users
    setShowBooks(false);
  };

  const deleteBook = async (id) => {
    try {
      await axios.delete(`http://localhost:8080/api/books/${id}`);
      // After successful deletion, fetch the updated list of books
      fetchBooks();
    } catch (error) {
      console.error('Error deleting book:', error);
    }
  };

  const deleteUser = async (id) => {
    try {
      await axios.delete(`http://localhost:8080/api/users/${id}`);
      // After successful deletion, fetch the updated list of users
      fetchUsers();
    } catch (error) {
      console.error('Error deleting user:', error);
    }
  };

  const handleSearchByName = async () => {
    try {
      const response = await axios.get(`http://localhost:8080/api/books/name/${bookNameQuery}`);
      setSelectedBook(response.data);
    } catch (error) {
      console.error('Error fetching book by name:', error);
    }
  };

  const handleInputChange = (event) => {
    const inputValue = event.target.value;
    setBookNameQuery(inputValue);
    if (inputValue === '') {
      setSelectedBook(null); // Reset selectedBook when input is empty
    }
  };
  return (
    <div className="dashboard-container">
       <h2 className="dashboard-heading">Dashboard</h2>

      <div className="search-form">
        <input
          type="text"
          placeholder="Search by Book Name"
          value={bookNameQuery}
          onChange={handleInputChange}
        />
        <button onClick={handleSearchByName}>Search</button>
      </div>

      <BookDetails book={selectedBook} show={selectedBook !== null} />

      <div className="dashboard-content">
        <div className="sidenav">
          <button onClick={toggleBooks}>{showBooks ? 'Hide Books' : 'Show All Books'}</button>
          <button onClick={toggleUsers}>{showUsers ? 'Hide Users' : 'Show All Users'}</button>
          <Link to="/add-book">Add Book</Link>
          <Link to="/reglog">Logout</Link>
        </div>

        {showBooks && (
          <div className="book-table">
            <h3>Books</h3>
            <table>
              <thead>
                <tr>
                  <th>Title</th>
                  <th>Author</th>
                  <th>Genre</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {books.map(book => (
                  <tr key={book.id}>
                    <td>{book.title}</td>
                    <td>{book.author}</td>
                    <td>{book.genre}</td>
                    <td>
                      <Link to={`/edit-book/${book.id}`}>
                        <button>Edit</button>
                      </Link>
                      <button onClick={() => deleteBook(book.id)}>Delete</button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}

        {showUsers && (
          <div className="user-table">
            <h3>Users</h3>
            <table>
              <thead>
                <tr>
                  <th>Username</th>
                  <th>Email</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {users.map(user => (
                  <tr key={user.id}>
                    <td>{user.username}</td>
                    <td>{user.email}</td>
                    <td>
                      <button onClick={() => deleteUser(user.id)}>Delete</button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
