import React, { useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const LoginUser = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');

  const handleLogin = async () => {
    try {
      const response = await axios.post('http://localhost:8080/api/users/login', {
        username: username,
        password: password
      });
      setMessage(response.data);
      // Redirect to dashboard or perform any necessary action upon successful login
    } catch (error) {
      setMessage(error.response.data);
    }
  };

  // Check if the message indicates that the user does not exist
  const isNewUser = message && message.toLowerCase().includes('does not exist');

  return (
    <div>
      <h2>User Login</h2>
      <input
        type="text"
        placeholder="Username"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
      />
      <br />
      <input
        type="password"
        placeholder="Password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <br />
      <button onClick={handleLogin}>Login</button>
      {isNewUser && (
        <div>
          <p>User does not exist. </p>
          <Link to="/register">Register</Link>
        </div>
      )}
      <p>{message}</p>
    </div>
  );
};

export default LoginUser;
