import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const Dashboard = () => {
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showBooks, setShowBooks] = useState(false); // State to track visibility of books

  useEffect(() => {
    fetchBooks();
  }, []);

  const fetchBooks = async () => {
    try {
      const response = await axios.get('http://localhost:8080/api/books');
      setBooks(response.data);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching books:', error);
    }
  };

  const toggleBooks = () => {
    setShowBooks(!showBooks); // Toggle the visibility of books
  };

  const renderBooks = () => {
    if (loading) {
      return <p>Loading...</p>;
    } else {
      return (
        <table style={{ display: showBooks ? 'block' : 'none', borderCollapse: 'collapse', width: '100%' }}>
          <thead>
            <tr>
              <th style={{ border: '1px solid #ddd', padding: '8px', textAlign: 'left', backgroundColor: '#f2f2f2' }}>Title</th>
              <th style={{ border: '1px solid #ddd', padding: '8px', textAlign: 'left', backgroundColor: '#f2f2f2' }}>Author</th>
              <th style={{ border: '1px solid #ddd', padding: '8px', textAlign: 'left', backgroundColor: '#f2f2f2' }}>Genre</th>
            </tr>
          </thead>
          <tbody>
            {books.map(book => (
              <tr key={book.id}>
                <td style={{ border: '1px solid #ddd', padding: '8px' }}>{book.title}</td>
                <td style={{ border: '1px solid #ddd', padding: '8px' }}>{book.author}</td>
                <td style={{ border: '1px solid #ddd', padding: '8px' }}>{book.genre}</td>
              </tr>
            ))}
          </tbody>
        </table>
      );
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2 style={{ marginBottom: '20px' }}>Dashboard</h2>
<br></br>
      <Link to="/add-book">
        <button style={{ marginRight: '10px' }}>Add Book</button>
      </Link>
      <button onClick={toggleBooks} style={{ marginRight: '10px' }}>
        {showBooks ? 'Hide Books' : 'Show All Books'}
      </button>
      
      <br />

      {renderBooks()}
    </div>
  );
};

export default Dashboard;
