import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const Dashboard = () => {
  const [books, setBooks] = useState([]);
  const [users, setUsers] = useState([]);
  const [loadingBooks, setLoadingBooks] = useState(true);
  const [loadingUsers, setLoadingUsers] = useState(true);
  const [showBooks, setShowBooks] = useState(false);
  const [showUsers, setShowUsers] = useState(false);

  useEffect(() => {
    fetchBooks();
    fetchUsers();
  }, []);

  const fetchBooks = async () => {
    try {
      const response = await axios.get('http://localhost:8080/api/books');
      setBooks(response.data);
      setLoadingBooks(false);
    } catch (error) {
      console.error('Error fetching books:', error);
    }
  };

  const fetchUsers = async () => {
    try {
      const response = await axios.get('http://localhost:8080/api/users');
      setUsers(response.data);
      setLoadingUsers(false);
    } catch (error) {
      console.error('Error fetching users:', error);
    }
  };

  const toggleBooks = () => {
    setShowBooks(!showBooks);
    // Hide user details when toggling books
    setShowUsers(false);
  };

  const toggleUsers = () => {
    setShowUsers(!showUsers);
    // Hide book details when toggling users
    setShowBooks(false);
  };

  const renderBooks = () => {
    if (loadingBooks) {
      return <p>Loading books...</p>;
    } else {
      return (
        <div style={{ display: showBooks ? 'block' : 'none' }}>
          <h3>Books</h3>
          <ul>
            {books.map(book => (
              <li key={book.id}>{book.title} by {book.author}</li>
            ))}
          </ul>
        </div>
      );
    }
  };

  const renderUsers = () => {
    if (loadingUsers) {
      return <p>Loading users...</p>;
    } else {
      return (
        <div style={{ display: showUsers ? 'block' : 'none' }}>
          <h3>Users</h3>
          <ul>
            {users.map(user => (
              <li key={user.id}>{user.username} - {user.email}</li>
            ))}
          </ul>
        </div>
      );
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <div style={{ display: 'flex', alignItems: 'center' }}>
        <h2 style={{ marginBottom: '20px', marginRight: 'auto' }}>Welcome to Library Management Dashboard</h2>
        <Link to="/reglog" style={{ position: 'relative', top: '20px', right: '20px' }}>
          <button>Logout</button>
        </Link>
      </div>

      <Link to="/add-book">
        <button style={{ marginRight: '10px' }}>Add Book</button>
      </Link>
      <button onClick={toggleBooks} style={{ marginRight: '10px' }}>
        {showBooks ? 'Hide Books' : 'Show All Books'}
      </button>
      <button onClick={toggleUsers} style={{ marginRight: '10px' }}>
        {showUsers ? 'Hide Users' : 'Show All Users'}
      </button>
      <br /><br />
      {renderBooks()}
      {renderUsers()}
    </div>
  );
};

export default Dashboard;
