import React, { useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import Dashboard from './DashBoard';

const LoginUser = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  const [loggedIn, setLoggedIn] = useState(false); // State to track login status

  const handleLogin = async () => {
    try {
      const response = await axios.post('http://localhost:8080/api/users/login', {
        username: username,
        password: password
      });
      setMessage(response.data);
      setLoggedIn(true);
    } catch (error) {
      setMessage(error.response.data);
    }
  };

  // Conditional rendering based on login status
  if (!loggedIn) {
    return (
      <div style={{ textAlign: 'center', margin: '50px auto', maxWidth: '400px', border: '1px solid #ccc', padding: '20px', borderRadius: '5px' }}>
        <h2 style={{ marginBottom: '20px', color: '#333' }}>User Login</h2>
        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          style={{ marginBottom: '10px', padding: '5px', width: '100%', border: '1px solid #ccc', borderRadius: '3px' }}
        />
        <br />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          style={{ marginBottom: '10px', padding: '5px', width: '100%', border: '1px solid #ccc', borderRadius: '3px' }}
        />
        <br />
        <button onClick={handleLogin} style={{ padding: '8px 16px', fontSize: '16px', backgroundColor: '#007bff', color: '#fff', border: 'none', borderRadius: '3px', cursor: 'pointer', marginBottom: '10px' }}>Login</button>
        <p style={{ margin: '10px 0', fontSize: '14px', color: 'red' }}>{message}</p>
        <p style={{ fontSize: '14px', color: '#333' }}>If you don't have an account, <Link to="/register" style={{ color: '#007bff', textDecoration: 'none' }}>register here</Link>.</p>
      </div>
    );
  } else {
    return <Dashboard />; // Render Dashboard component after successful login
  }
};

export default LoginUser;
