import React, { useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import Dashboard from './DashBoard';

const LoginUser = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  const [loggedIn, setLoggedIn] = useState(false); // State to track login status

  const handleLogin = async () => {
    try {
      const response = await axios.post('http://localhost:8080/api/users/login', {
        username: username,
        password: password
      });
      setMessage(response.data);
      setLoggedIn(true);
    } catch (error) {
      setMessage(error.response.data);
    }
  };

  // Conditional rendering based on login status
  if (!loggedIn) {
    return (
      <div style={{ textAlign: 'center', margin: '50px auto', maxWidth: '400px' }}>
        <h2>User Login</h2>
        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          style={{ marginBottom: '10px', padding: '5px' }}
        />
        <br />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          style={{ marginBottom: '10px', padding: '5px' }}
        />
        <br />
        <button onClick={handleLogin} style={{ padding: '8px 16px', fontSize: '16px' }}>Login</button>
        <p style={{ margin: '10px 0', fontSize: '14px' }}>{message}</p>
        <p style={{ fontSize: '14px' }}>If you don't have an account, <Link to="/register">register here</Link>.</p>
      </div>
    );
  } else {
    return <Dashboard />; // Render Dashboard component after successful login
  }
};

export default LoginUser;
