import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import './DashBoard.css';

const Dashboard = () => {
  const [books, setBooks] = useState([]);
  const [users, setUsers] = useState([]);
  const [loadingBooks, setLoadingBooks] = useState(true);
  const [loadingUsers, setLoadingUsers] = useState(true);
  const [showBooks, setShowBooks] = useState(false);
  const [showUsers, setShowUsers] = useState(false);
  const [authorQuery, setAuthorQuery] = useState('');
  const [bookNameQuery, setBookNameQuery] = useState('');
  const [genreQuery, setGenreQuery] = useState('');

  useEffect(() => {
    fetchBooks();
    fetchUsers();
  }, []);

  const fetchBooks = async () => {
    try {
      const response = await axios.get('http://localhost:8080/api/books');
      setBooks(response.data);
      setLoadingBooks(false);
    } catch (error) {
      console.error('Error fetching books:', error);
    }
  };

  const fetchUsers = async () => {
    try {
      const response = await axios.get('http://localhost:8080/api/users');
      setUsers(response.data);
      setLoadingUsers(false);
    } catch (error) {
      console.error('Error fetching users:', error);
    }
  };

  const toggleBooks = () => {
    setShowBooks(!showBooks);
    // Hide user details when toggling books
    setShowUsers(false);
  };

  const toggleUsers = () => {
    setShowUsers(!showUsers);
    // Hide book details when toggling users
    setShowBooks(false);
  };

  const deleteBook = async (id) => {
    try {
      await axios.delete(`http://localhost:8080/api/books/${id}`);
      // After successful deletion, fetch the updated list of books
      fetchBooks();
    } catch (error) {
      console.error('Error deleting book:', error);
    }
  };

  const deleteUser = async (id) => {
    try {
      await axios.delete(`http://localhost:8080/api/users/${id}`);
      // After successful deletion, fetch the updated list of users
      fetchUsers();
    } catch (error) {
      console.error('Error deleting user:', error);
    }
  };

  const filteredBooks = books.filter((book) => {
    return (
      book.author.toLowerCase().includes(authorQuery.toLowerCase()) &&
      book.title.toLowerCase().includes(bookNameQuery.toLowerCase()) &&
      book.genre.toLowerCase().includes(genreQuery.toLowerCase())
    );
  });

  const renderBooks = () => {
    if (loadingBooks) {
      return <p>Loading books...</p>;
    } else {
      return (
        <div className="book-table" style={{ display: showBooks ? 'block' : 'none' }}>
          <h3>Books</h3>
          <div className="search-form">
            <input
              type="text"
              placeholder="Search by Author"
              value={authorQuery}
              onChange={(e) => setAuthorQuery(e.target.value)}
            />
            <input
              type="text"
              placeholder="Search by Book Name"
              value={bookNameQuery}
              onChange={(e) => setBookNameQuery(e.target.value)}
            />
            <input
              type="text"
              placeholder="Search by Genre"
              value={genreQuery}
              onChange={(e) => setGenreQuery(e.target.value)}
            />
          </div>
          <table>
            <thead>
              <tr>
                <th>Title</th>
                <th>Author</th>
                <th>Genre</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {filteredBooks.map(book => (
                <tr key={book.id}>
                  <td>{book.title}</td>
                  <td>{book.author}</td>
                  <td>{book.genre}</td>
                  <td>
                    <Link to={`/edit-book/${book.id}`}>
                      <button>Edit</button>
                    </Link>
                    <button onClick={() => deleteBook(book.id)}>Delete</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      );
    }
  };

  const renderUsers = () => {
    if (loadingUsers) {
      return <p>Loading users...</p>;
    } else {
      return (
        <div className="user-table" style={{ display: showUsers ? 'block' : 'none' }}>
          <h3>Users</h3>
          <table>
            <thead>
              <tr>
                <th>Username</th>
                <th>Email</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {users.map(user => (
                <tr key={user.id}>
                  <td>{user.username}</td>
                  <td>{user.email}</td>
                  <td>
                    <button onClick={() => deleteUser(user.id)}>Delete</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      );
    }
  };

  return (
    <div className="dashboard-container">
      <h2 className="dashboard-heading">Dashboard</h2>
      <div className="dashboard-content">
        <div className="sidenav">
          <Link to="/add-book">
            <button>Add Book</button>
          </Link>
          <button onClick={toggleBooks}>
            {showBooks ? 'Hide Books' : 'Show All Books'}
          </button>
          <button onClick={toggleUsers}>
            {showUsers ? 'Hide Users' : 'Show All Users'}
          </button>
          <Link to="/reglog">
            <button>Logout</button>
          </Link>
        </div>
        {renderBooks()}
        {renderUsers()}
      </div>
    </div>
  );
};

export default Dashboard;
