import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const Dashboard = () => {
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showBooks, setShowBooks] = useState(false); // State to track visibility of books

  useEffect(() => {
    fetchBooks();
  }, []);

  const fetchBooks = async () => {
    try {
      const response = await axios.get('http://localhost:8080/api/books');
      setBooks(response.data);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching books:', error);
    }
  };

  const toggleBooks = () => {
    setShowBooks(!showBooks); // Toggle the visibility of books
  };

  const renderBooks = () => {
    if (loading) {
      return <p>Loading...</p>;
    } else {
      return (
        <div style={{ display: showBooks ? 'block' : 'none' }}>
          <h3>All Books</h3>
          <ul>
            {books.map(book => (
              <li key={book.id}>
                {book.title} - {book.author} - {book.genre}
              </li>
            ))}
          </ul>
        </div>
      );
    }
  };

  return (
    <div>
      <h2>Dashboard</h2>

      <Link to="/add-book">
        <button>Add Book</button>
      </Link>

      <button onClick={toggleBooks}>
        {showBooks ? 'Hide Books' : 'Show All Books'}
      </button>

      {renderBooks()}
    </div>
  );
};

export default Dashboard;
