import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import './DashBoard.css';
import BookDetails from './book/BookDetails';
const Dashboard = () => {
  const [books, setBooks] = useState([]);
  const [users, setUsers] = useState([]);
  const [loadingBooks, setLoadingBooks] = useState(true);
  const [loadingUsers, setLoadingUsers] = useState(true);
  const [showBooks, setShowBooks] = useState(false);
  const [showUsers, setShowUsers] = useState(false);
  const [authorQuery, setAuthorQuery] = useState('');
  const [bookNameQuery, setBookNameQuery] = useState('');
  const [genreQuery, setGenreQuery] = useState('');
  const [selectedBook, setSelectedBook] = useState(null);
  useEffect(() => {
    fetchBooks();
    fetchUsers();
  }, []);

  const fetchBooks = async () => {
    try {
      const response = await axios.get('http://localhost:8080/api/books');
      setBooks(response.data);
      setLoadingBooks(false);
    } catch (error) {
      console.error('Error fetching books:', error);
    }
  };

  const fetchUsers = async () => {
    try {
      const response = await axios.get('http://localhost:8080/api/users');
      setUsers(response.data);
      setLoadingUsers(false);
    } catch (error) {
      console.error('Error fetching users:', error);
    }
  };

  const toggleBooks = () => {
    setShowBooks(!showBooks);
    // Hide user details when toggling books
    setShowUsers(false);
  };

  const toggleUsers = () => {
    setShowUsers(!showUsers);
    // Hide book details when toggling users
    setShowBooks(false);
  };

  const deleteBook = async (id) => {
    try {
      await axios.delete(`http://localhost:8080/api/books/${id}`);
      
      fetchBooks();
    } catch (error) {
      console.error('Error deleting book:', error);
    }
  };

  const deleteUser = async (id) => {
    try {
      await axios.delete(`http://localhost:8080/api/users/${id}`);
     
      fetchUsers();
    } catch (error) {
      console.error('Error deleting user:', error);
    }
  };

  const renderBooks = () => {
    if (loadingBooks) {
      return <p>Loading books...</p>;
    } else {
      return (
        <div className="book-table" style={{ display: showBooks ? 'block' : 'none' }}>
          <h3 style={{ textAlign: 'center', marginBottom: '10px' }}>Books</h3>
          <table>
            <thead>
              <tr>
                <th>Title</th>
                <th>Author</th>
                <th>Genre</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {filteredBooks.map(book => (
                <tr key={book.id}>
                  <td>{book.title}</td>
                  <td>{book.author}</td>
                  <td>{book.genre}</td>
                  <td>
                    <Link to={`/edit-book/${book.id}`}>
                      <button>Edit</button>
                    </Link>
                    <button onClick={() => deleteBook(book.id)}>Delete</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      );
    }
  };
  

  const filteredBooks = books.filter((book) => {
    return (
      book.author.toLowerCase().includes(authorQuery.toLowerCase()) &&
      book.title.toLowerCase().includes(bookNameQuery.toLowerCase()) &&
      book.genre.toLowerCase().includes(genreQuery.toLowerCase())
    );
  });

  const handleSearchByName = async () => {
    try {
      const response = await axios.get(`http://localhost:8080/api/books/name/${bookNameQuery}`);
      setSelectedBook(response.data);
    } catch (error) {
      console.error('Error fetching book by name:', error);
    }
  };

  const renderUsers = () => {
    if (loadingUsers) {
      return <p>Loading users...</p>;
    } else {
      return (
        <div className="user-table" style={{ display: showUsers ? 'block' : 'none' }}>
          <h3 style={{ textAlign: 'center', marginBottom: '10px' }}>Users</h3>
          <table>
            <thead>
              <tr>
                <th>Username</th>
                <th>Email</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {users.map(user => (
                <tr key={user.id}>
                  <td>{user.username}</td>
                  <td>{user.email}</td>
                  <td>
                    <button onClick={() => deleteUser(user.id)}>Delete</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      );
    }
  };

  return (
    <div className="dashboard-container">
      <h2 style={{ position: 'fixed', top: '0', left: '50%', transform: 'translateX(-50%)', zIndex: '9999',marginBottom: '20px',
    color: 'yellow',
    fontSize: '30px',
    textShadow: '2px 2px 4px rgba(0, 0, 0, 0.5)', 
    fontWeight: 'bold', 
    letterSpacing: '1px', 
    position: 'fixed', top: '0', left: '50%', transform: 'translateX(-50%)', zIndex: '9999' }}>Dashboard</h2>



<div className="search-form">
        <input
          type="text"
          placeholder="Search by Book Name"
          value={bookNameQuery}
          onChange={(e) => setBookNameQuery(e.target.value)}
        />
        <button onClick={handleSearchByName}>Search</button>
      </div>

      {selectedBook && <BookDetails book={selectedBook} />}

      <div className="dashboard-content">
        <br />
        <div className="sidenav">
       <button style={{ width: '150px', height: '40px', fontSize: '16px',top:'180px',marginLeft: '10px' }}>Library System</button>
       <br></br><br></br>
        <Link to="/add-book">
       <button style={{ width: '150px', height: '40px', fontSize: '16px',top:'200px',marginLeft: '-5px' }}>Add Book</button>
         </Link>
    <br />
    <button onClick={toggleBooks} style={{ width: '150px', height: '40px', fontSize: '16px', marginLeft: '10px',top:'220px' }}>
    {showBooks ? 'Hide Books' : 'Show All Books'}
    </button>
    <br></br><br></br>
      <button onClick={toggleUsers} style={{ width: '150px', height: '40px', fontSize: '16px', marginLeft: '10px', top: '240px' }}>
    {showUsers ? 'Hide Users' : 'Show All Users'}
    </button>
    
  <Link to="/reglog" style={{ position: 'absolute', top: '280px', right: '25px', width: '150px', height: '40px' }}>
  <button style={{ width: '150px', height: '40px',fontSize: '16px' }}>Logout</button>
  </Link>

        </div>
        
        {renderBooks()}
        {renderUsers()}
      </div>
    </div>
  );
};

export default Dashboard;
