import React, { useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const LoginUser = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  const [loggedIn, setLoggedIn] = useState(false); // State to track login status

  const handleLogin = async () => {
    try {
      const response = await axios.post('http://localhost:8080/api/users/login', {
        username: username,
        password: password
      });
      setMessage(response.data);
      setLoggedIn(true); // Set loggedIn state to true upon successful login
    } catch (error) {
      setMessage(error.response.data);
    }
  };

  // Render login form if not logged in, otherwise redirect to Dashboard
  if (!loggedIn) {
    return (
      <div>
        <h2>User Login</h2>
        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
        <br />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <br />
        <button onClick={handleLogin}>Login</button>
        <p>{message}</p>
        <p>If you don't have an account, <Link to="/register">register here</Link>.</p>
      </div>
    );
  } else {
    return <Redirect to="/dashboard" />; // Redirect to Dashboard page if logged in
  }
};

export default LoginUser;
