import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import './DashBoard.css';
const Dashboard = () => {
  const [books, setBooks] = useState([]);
  const [users, setUsers] = useState([]);
  const [loadingBooks, setLoadingBooks] = useState(true);
  const [loadingUsers, setLoadingUsers] = useState(true);
  const [showBooks, setShowBooks] = useState(false);
  const [showUsers, setShowUsers] = useState(false);

  useEffect(() => {
    fetchBooks();
    fetchUsers();
  }, []);

  const fetchBooks = async () => {
    try {
      const response = await axios.get('http://localhost:8080/api/books');
      setBooks(response.data);
      setLoadingBooks(false);
    } catch (error) {
      console.error('Error fetching books:', error);
    }
  };

  const fetchUsers = async () => {
    try {
      const response = await axios.get('http://localhost:8080/api/users');
      setUsers(response.data);
      setLoadingUsers(false);
    } catch (error) {
      console.error('Error fetching users:', error);
    }
  };

  const toggleBooks = () => {
    setShowBooks(!showBooks);
    // Hide user details when toggling books
    setShowUsers(false);
  };

  const toggleUsers = () => {
    setShowUsers(!showUsers);
    // Hide book details when toggling users
    setShowBooks(false);
  };

  const deleteBook = async (id) => {
    try {
      await axios.delete(`http://localhost:8080/api/books/${id}`);
      // After successful deletion, fetch the updated list of books
      fetchBooks();
    } catch (error) {
      console.error('Error deleting book:', error);
    }
  };

  const deleteUser = async (id) => {
    try {
      await axios.delete(`http://localhost:8080/api/users/${id}`);
      // After successful deletion, fetch the updated list of users
      fetchUsers();
    } catch (error) {
      console.error('Error deleting user:', error);
    }
  };

  const renderBooks = () => {
    if (loadingBooks) {
      return <p>Loading books...</p>;
    } else {
      return (
        <div style={{ display: showBooks ? 'block' : 'none' }}>
          <h3>Books</h3>
          <table>
            <thead>
              <tr>
                <th>Title</th>
                <th>Author</th>
                <th>Genre</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {books.map(book => (
                <tr key={book.id}>
                  <td>{book.title}</td>
                  <td>{book.author}</td>
                  <td>{book.genre}</td>
                  <td>
                    <div className="sidenav">
                    <Link to={`/edit-book/${book.id}`}>
                      <button>Edit</button>
                    </Link></div>
                    <button onClick={() => deleteBook(book.id)}>Delete</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      );
    }
  };

  const renderUsers = () => {
    if (loadingUsers) {
      return <p>Loading users...</p>;
    } else {
      return (
        <div style={{ display: showUsers ? 'block' : 'none' }}>
          <h3>Users</h3>
          <table>
            <thead>
              <tr>
                <th>Username</th>
                <th>Email</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {users.map(user => (
                <tr key={user.id}>
                  <td>{user.username}</td>
                  <td>{user.email}</td>
                  <td>
                    <button onClick={() => deleteUser(user.id)}>Delete</button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      );
    }
  };

  return (
    <div style={{ padding: '20px', display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
      <h2>Welcome to Library Management Dashboard</h2>
      <Link to="/reglog" style={{ position: 'absolute', top: '20px', right: '20px' }}>
        <button>Logout</button>
      </Link>
      <br />
      <Link to="/add-book">
        <button>Add Book</button>
      </Link>
      <button onClick={toggleBooks} style={{ marginLeft: '10px' }}>{showBooks ? 'Hide Books' : 'Show All Books'}</button>
      <button onClick={toggleUsers} style={{ marginLeft: '10px' }}>{showUsers ? 'Hide Users' : 'Show All Users'}</button>
      <br /><br />
      {renderBooks()}
      {renderUsers()}
    </div>
  );
};

export default Dashboard;
