import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import './DashBoard.css';

const Dashboard = () => {
  const [books, setBooks] = useState([]);
  const [users, setUsers] = useState([]);
  const [loadingBooks, setLoadingBooks] = useState(true);
  const [loadingUsers, setLoadingUsers] = useState(true);
  const [showBooks, setShowBooks] = useState(false);
  const [showUsers, setShowUsers] = useState(false);

  useEffect(() => {
    fetchBooks();
    fetchUsers();
  }, []);

  const fetchBooks = async () => {
    try {
      const response = await axios.get('http://localhost:8080/api/books');
      setBooks(response.data);
      setLoadingBooks(false);
    } catch (error) {
      console.error('Error fetching books:', error);
    }
  };

  const fetchUsers = async () => {
    try {
      const response = await axios.get('http://localhost:8080/api/users');
      setUsers(response.data);
      setLoadingUsers(false);
    } catch (error) {
      console.error('Error fetching users:', error);
    }
  };

  const toggleBooks = () => {
    setShowBooks(!showBooks);
    // Hide user details when toggling books
    setShowUsers(false);
  };

  const toggleUsers = () => {
    setShowUsers(!showUsers);
    // Hide book details when toggling users
    setShowBooks(false);
  };

  const renderBooks = () => {
    if (loadingBooks) {
      return <p>Loading books...</p>;
    } else {
      return (
        <div style={{ display: showBooks ? 'block' : 'none' }}>
          <h3>Books</h3>
          <ul>
            {books.map(book => (
              <li key={book.id}>{book.title} by {book.author}</li>
            ))}
          </ul>
        </div>
      );
    }
  };

  const renderUsers = () => {
    if (loadingUsers) {
      return <p>Loading users...</p>;
    } else {
      return (
        <div style={{ display: showUsers ? 'block' : 'none' }}>
          <h3>Users</h3>
          <ul>
            {users.map(user => (
              <li key={user.id}>{user.username} - {user.email}</li>
            ))}
          </ul>
        </div>
      );
    }
  };

  return (
    <div style={{ padding: '20px', backgroundImage: 'url("https://i.pinimg.com/736x/1a/13/3e/1a133eb95a64380ca00ecf7fb7c8f33c.jpg")', backgroundSize: 'cover', backgroundRepeat: 'no-repeat', minHeight: '100vh', position: 'relative' }}>
      <div style={{ display: 'flex', alignItems: 'center' }}>
        <h2 style={{ marginBottom: '20px', marginRight: 'auto' }}>Welcome to Library Management Dashboard</h2>
        <Link to="/reglog" style={{ position: 'relative', top: '20px', right: '20px' }}>
          <button>Logout</button>
        </Link>
      </div>

      <div className="sidebar">
        <Link to="/add-book">
          <button>Add Book</button>
        </Link>
        <button onClick={toggleBooks}>
          {showBooks ? 'Hide Books' : 'Show All Books'}
        </button>
        <button onClick={toggleUsers}>
          {showUsers ? 'Hide Users' : 'Show All Users'}
        </button>
      </div>

      {renderBooks()}
      {renderUsers()}
    </div>
  );
};

export default Dashboard;
